package LoopStatements;

public class Count {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
	     int num = 100;
		 int count = 0;
	     while(num != 0)
	     {
	        num = num / 10;
	        count++;
	     }
	      System.out.println("Count of the number is"+count);

	}

}
